import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Book = () => {
  /* individual Books shelf  */

  const [inBook, setInBook] = useState({});

  let { id } = useParams();

  const handleOnClick = () => {

    axios.get("http://localhost:8080/books/:id",{params:id}).then((response) => setInBook(response.data))
  }

  useEffect(() => {
    handleOnClick()
  }, []);

  return (
    <div>
      <h3>Book: {inBook.title}</h3>
      <small>
        <em>
          Author:<b>{inBook.author}</b>, ReleaseDate: {inBook.releaseDate}
        </em>
      </small>
      <hr />
    </div>
  );
};

export default Book;
