import AtomicHabits from "../images/BooksImg/atomicHabits.jpg";
import DieHard from "../images/BooksImg/DieHard.jpg";
import HungerGames from "../images/BooksImg/TheHungerGames.jpg";
import WorldWarZ from "../images/BooksImg/WorldWarZ.jpg";
import Martian from "../images/BooksImg/TheMartian.jpg";
import Slider from "react-slick";

const BookCard = () => {
  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlides: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <div>
      <h2>responsive</h2>
      <Slider {...settings}>
        {[1, 2, 3, 4].map((item, id) => {
          return <div key={id}>{item}</div>;
        })}
      </Slider>
    </div>
  );
};

export default BookCard;
