import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Book = () => {
  /* individual Books shelf  */
  const [myBook, setMyBook] = useState({});

  let { id } = useParams();

  useEffect(() => {
    let book = [
      {
        id: 1,
        title: "Atomic Habbits",
        author: "James Clear",
        releaseDate: "16 Oct 2018",
        rating: "4.8",
      },
      {
        id: 2,
        title: "The Stranger",
        author: "Albert Camus",
        releaseDate: "19 May 1942",
        rating: "4.4",
      },
      {
        id: 3,
        title: "The Old Man And The Sea",
        author: "Ernest Hemingway",
        releaseDate: "1952",
        rating: "4.2",
      },
    ];

    setMyBook(book);
  }, [id]);

  return (
    <div>
      <h3>Book: {myBook.title}</h3>
      <small><em>Author:{myBook.author}, ReleaseDate: {myBook.releaseDate}</em></small>
      <hr />
    </div>
  );
};

export default Book;
