import { Component } from "react";
import AtomicHabit from "./images/BooksImg/atomicHabits.jpg";
import HungerGames from "./images/BooksImg/TheHungerGames.jpg";
import WorldWar from "./images/BooksImg/WorldWarZ.jpg";
import DieHard from "./images/BooksImg/DieHard.jpg";
import PlayerOne from "./images/BooksImg/TheReadyPlayerOne.jpg";
import { ScrollingCarousel } from "@trendyol-js/react-carousel";

export default class Books extends Component {
  render() {
    let Images = [
      {
        id: 1,
        title: "Atomic Habits",
        ImgSource: AtomicHabit,
      },
      {
        id: 2,
        title: "Die Hard",
        ImgSource: DieHard
      },
      {
        id: 3,
        title: "The Hunger Games",
        ImgSource: HungerGames
      },
      {
        id: 4,
        title: "World War Z",
        ImgSource: WorldWar
      },
      {
        id: 5,
        title: "Ready Player One",
        ImgSource: PlayerOne
      },
    ];
    return (
      <div className="container">
        <div className="row">
          <div className="col">
            <h3 className="mx-1 fs-4" style={{ fontFamily: "sans-serif" }}>
              Top Books
            </h3>
            <ScrollingCarousel className="m-1">
              {Images.map((image) => {
               return  <div key={image.id}>
                  <img src={image.ImgSource} className="img-fluid rounded" style={{height: "200px"}} alt="...."/>
                  <p>{image.title}</p>
                </div>
              })
              }
            </ScrollingCarousel>
          </div>
        </div>
      </div>
    );
  }
}
