import AtomicHabits from "../images/BooksImg/atomicHabits.jpg";
import DieHard from "../images/BooksImg/DieHard.jpg";
import HungerGames from "../images/BooksImg/TheHungerGames.jpg";
import WorldWarZ from "../images/BooksImg/WorldWarZ.jpg";
import Martian from "../images/BooksImg/TheMartian.jpg";
import { useState } from "react";
import Swiper, { SwiperSlide } from "swiper/react";
import { Pagination } from "react-bootstrap";
import "swiper/modules/navigation";
import { useNavigation } from "react-router-dom";

const BookCard = () => {
  const [swipeRef, setSwipeRef] = useState(null);

  const nav = useNavigation();
  return (
    <>
      <Swiper
        onSwiper={setSwipeRef}
        slidesPerView={true}
        spaceBetween={30}
        pagination={{ type: "fraction" }}
        navigation={true}
        modules={[Pagination, nav]}
        className="mySwiper"
      >
        <SwiperSlide>
          <img src={AtomicHabits} className="img-fluid" alt="..." />
        </SwiperSlide>
        <SwiperSlide>
          <img src={HungerGames} className="img-fluid" alt="..." />
        </SwiperSlide>
        <SwiperSlide>
          <img src={WorldWarZ} className="img-fluid" alt="..." />
        </SwiperSlide>
        <SwiperSlide>
          <img src={DieHard} className="img-fluid" alt="..." />
        </SwiperSlide>
      </Swiper>
    </>
  );
};

export default BookCard;
