import AtomicHabits from "../images/BooksImg/atomicHabits.jpg";
import DieHard from "../images/BooksImg/DieHard.jpg";
import HungerGames from "../images/BooksImg/TheHungerGames.jpg";
import WorldWarZ from "../images/BooksImg/WorldWarZ.jpg";
import Martian from "../images/BooksImg/TheMartian.jpg";
import React, { Component } from "react";
import Slider from "react-slick";

export default class BookCard extends Component{

  render(){
    const setting = {
      className: "center",
      centerMode: true,
      infinite: true,
      centerPadding: "60px",
      slidesToShow: 3,
      speed: 500
    };
  return(
  <div className="container">
        <div className="row">
        <Slider {...setting}>
            <div>
              <img src={AtomicHabits} className="img-fluid rounded" alt="..."/>
            </div>
            <div>
              <img src={HungerGames} className="img-fluid rounded" alt="..."/>
            </div>
            <div>
              <img src={WorldWarZ} className="img-fluid rounded" alt="..."/>
            </div>
            <div>
              <img src={Martian} className="img-fluid rounded" alt="..."/>
            </div>
            <div>
              <img src={DieHard} className="img-fluid rounded" alt="..."/>
            </div>
        </Slider>
        </div>
  </div>
  );
  }
}
