import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Book = (props) => {
  /* individual Books shelf  */

  const name = props.name;
  console.log(name);

  const [inBook, setInBook] = useState({});

  useEffect(() => {
    const da = "Atomic Habits"
    const va = da
    var jsonObj = JSON.stringify(va)
    console.log(typeof jsonObj)
    axios
      .get("http://localhost8080/books/id", { params: jsonObj })
      .then((response) => {
        console.log(response.data);
        setInBook(response.data);
      });
  });

  return (
    <div>
      <h3>Book: {inBook.title}</h3>
      <small>
        <em>
          Id: {inBook.id}, Author:<b>{inBook.author}</b>, ReleaseDate:{" "}
          {inBook.releaseDate}
        </em>
      </small>
      <hr />
    </div>
  );
};

export default Book;
