import axios from "axios";
import { useEffect, useState } from "react";

const Book = (props) => {
  /* individual Books shelf  */

  const {name} = props.name;
  console.log("this is from child", name);

  const [inBook, setInBook] = useState({});


  useEffect(() => {
    axios.get("http://localhost:8080/books/id",{params: name})
    .then(function(response){
        return response.json();
      }).then(function(data){
        console.log(data)
        setInBook(data)
      })
    .catch((e) => {
        console.error(e)
      })
  });

  return (
    <div>
      <h3>Book: {inBook.title}</h3>
      <small>
        <em>
          Id: {inBook.id}, Author:<b>{inBook.author}</b>, ReleaseDate:{" "}
          {inBook.releaseDate}
        </em>
      </small>
      <hr />
    </div>
  );
};

export default Book;
