import { useState } from "react";
import { useNavigate, useOutletContext } from "react-router-dom";
import Input from "./form/Input";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { setJwtToken } = useOutletContext();
  const { setAlertClassName } = useOutletContext();
  const { setAlertMessage } = useOutletContext();

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const jsonObj = {
      email: email,
      password: password,
    };

    const jsonContainer = jsonObj;
    console.log(jsonContainer);

    var obj = JSON.stringify(jsonContainer);
    console.log(obj);

    fetch("http://localhost:8080/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: obj,
    })
      .then(function (response) {
        return response.json();
      })
      .then(function (data) {
        console.log("server response", data);
        try {
          if (data === 404) {
            setAlertClassName("alert-danger");
            setAlertMessage("Email not found");
          } else {
            navigate("/");
            setJwtToken(true);
            setAlertMessage("User is checked");
            setAlertClassName("d-none");
          }
        } catch {
          console.error(e);
        }
      })
      .catch((e) => {
        console.error(e);
      });
    // SET STATE TO NULL AFTER SUBMITTING THE FORM
    setEmail("");
    setPassword("");
  };

  return (
    <div className="col-md-6 offset-md-3">
      <h2>Login</h2>
      <hr />

      <form action="/login" method="post" onSubmit={handleSubmit}>
        <div className="form-floating mb-3">
          <input id="floatingEmail" className="form-control" type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="abc@gmail.com" autoComplete="email-new" required/>
          <label htmlFor="floatingEmail">Eamil Address</label>
        </div>
        <div className="form-floating">
        <Input
          title="Password"
          type="password"
          id="password"
          className="form-control"
          name="password"
          autoComplete="password-new"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        </div>
        <hr />

        <input type="submit" className="btn btn-primary" value="Login" />
      </form>
    </div>
  );
};

export default Login;
