      return 0;
    case 'FORMERR':
      return 1;
    case 'SERVFAIL':
      return 2;
    case 'NXDOMAIN':
      return 3;
    case 'NOTIMP':
      return 4;
    case 'REFUSED':
      return 5;
    case 'YXDOMAIN':
      return 6;
    case 'YXRRSET':
      return 7;
    case 'NXRRSET':
      return 8;
    case 'NOTAUTH':
      return 9;
    case 'NOTZONE':
      return 10;
    case 'RCODE_11':
      return 11;
    case 'RCODE_12':
      return 12;
    case 'RCODE_13':
      return 13;
    case 'RCODE_14':
      return 14;
    case 'RCODE_15':
      return 15;
  }
  return 0;
};
